```csharp
public static int[] QuickSort(int[] arr)
{
    if (arr.Length <= 1)
    {
        return arr;
    }

    int pivot = arr[arr.Length - 1];
    int[] left = new int[arr.Length - 1];
    int[] right = new int[arr.Length - 1];

    for (int i = 0; i < arr.Length; i++)
    {
        if (arr[i] <= pivot)
        {
            left[i] = arr[i];
        }
        else
        {
            right[i - 1] = arr[i];
        }
    }

    int[] leftSorted = QuickSort(left);
    int[] rightSorted = QuickSort(right);

    int[] sorted = new int[arr.Length];
    for (int i = 0; i < leftSorted.Length; i++)
    {
        sorted[i] = leftSorted[i];
    }
    for (int i = leftSorted.Length; i < arr.Length; i++)
    {
        sorted[i] = rightSorted[i - leftSorted.Length];
    }

    return sorted;
}
