1. Create a class for a version control system.
```cpp
class VersionControl {
private:
    int version; // version number
    string filename; // filename being tracked
    string content; // content of the file
    vector<string> history; // history of file changes

public:
    VersionControl(int v, string f, string c) {
        version = v;
        filename = f;
        content = c;
        history.push_back(c);
    }

    void add_change(string c) {
        content = c;
        history.push_back(c);
    }

    void commit_changes() {
        string new_content = content;
        for (int i = 0; i < history.size(); i++) {
            new_content += history[i] + "\n";
        }
        ofstream ofs(filename);
        ofs << new_content;
        ofs.close();
        content = new_content;
        history.clear();
    }

    void rollback_changes() {
        if (history.size() > 0) {
            string old_content = content;
            ofstream ofs(filename);
            ofs << old_content;
            ofs.close();
            content = old_content;
            history.clear();
        }
    }
};
```
