public List<ExcelData> ReadExcelTable(string filePath)
{
    var package = new ExcelPackage(new FileInfo(filePath));
    ExcelWorksheet sheet = package.Workbook.Worksheets[1];
    var table = sheet.Tables.First();

    var columnNames = new List<string>();
    var columnData = new List<ExcelData>();

    // Get the column names
    for (int i = 1; i <= table.UsedRange.Columns.Count; i++)
    {
        columnNames.Add(table.Cells[1, i].Value.ToString());
    }

    // Read the data into the columnData list
    for (int row = 2; row <= table.UsedRange.Rows.Count; row++)
    {
        var data = new ExcelData();
        data.Id = table.Cells[row, 1].Value.ToString();
        data.Name = table.Cells[row, 2].Value.ToString();
        data.Gender = table.Cells[row, 3].Value.ToString();
        columnData.Add(data);
    }

    // Categorize the data based on the column names
    var groupedData = new Dictionary<string, List<ExcelData>>();
    foreach (var column in columnData)
    {
        var key = columnNames[column.Idx - 1];
        if (!groupedData.ContainsKey(key))
        {
            groupedData.Add(key, new List<ExcelData>());
        }
        groupedData[key].Add(column);
    }

    return groupedData.Values.ToList();
}
