// Queue using two stacks
type Queue struct {
	inStack *[]int
	outStack *[]int
}

// Constructor function
func NewQueue() *Queue {
	inStack := []int{}
	outStack := []int{}
	queue := &Queue{
		inStack: &inStack,
		outStack: &outStack,
	}
	return queue
}

// Enqueue function
func (q *Queue) Enqueue(item int) {
	*q.inStack = append(*q.inStack, item)
}

// Dequeue function
func (q *Queue) Dequeue() int {
	if len(*q.outStack) == 0 {
		fmt.Println("Queue is empty")
		return -1
	}
	return (*q.outStack)[len(*q.outStack)-1]
}

// Front function
func (q *Queue) Front() int {
	if len(*q.outStack) == 0 {
		fmt.Println("Queue is empty")
		return -1
	}
	return (*q.outStack)[len(*q.outStack)-1]
}

// IsEmpty function
func (q *Queue) IsEmpty() bool {
	return len(*q.outStack) == 0
}

// String function
func (q Queue) String() string {
	return fmt.Sprintf("Queue: %v, InStack: %v, OutStack: %v", q, *q.inStack, *q.outStack)
}

func main() {
	queue := NewQueue()

	queue.Enqueue(1)
	queue.Enqueue(2)
	queue.Enqueue(3)

	fmt.Println("Initial queue:", queue)

	fmt.Println("Dequeued item:", queue.Dequeue())
	fmt.Println("Front item:", queue.Front())

	fmt.Println("Is the queue empty?", queue.IsEmpty())

