    public static void Sort(int[] arr)
    {
        if (arr == null || arr.Length == 0)
        {
            return;
        }

        int pivotIndex = Partition(arr, 0, arr.Length - 1);
        Sort(arr, 0, pivotIndex - 1);
        Sort(arr, pivotIndex + 1, arr.Length - 1);
