```c
#include <iostream>
#include <vector>
#include <algorithm>

using namespace std;

const int INF = 1e9;

struct GraphNode {
    int label, next;
};

struct Graph {
    int V, E;
    vector<GraphNode> adj[510]; // 510 is the maximum number of vertices in the graph

    Graph(int v, int e) {
        V = v; E = e;
    }

    void addEdge(int u, int v) {
        adj[u].next = v;
    }
};

int dijkstra(Graph g, int s) {
    int n = g.V;
    vector<int> dist(n, INF);
    vector<bool> visited(n, false);

    for (int i = 0; i < n; i++) {
        dist[i] = g.adj[i].next == INF ? INF : 0;
        visited[i] = true;
    }
    dist[s] = 0;

    int minDist = INF;
    for (int i = 0; i < n; i++) {
        if (visited[i]) continue;
        int u = i;
        while (u != s) {
            u = g.adj[u].next;
        }
        minDist = min(minDist, dist[u]);
    }

    return minDist;
}

int main() {
    Graph g(510, 1000); // Create a graph with 510 vertices and 1000 edges
    g.addEdge(0, 1);
    g.addEdge(0, 2);
    g.addEdge(0, 3);
    g.addEdge(1, 2);
    g.addEdge(2, 3);
    g.addEdge(2, 4);
    g.addEdge(3, 4);
    g.addEdge(3, 5);
    g.addEdge(4, 5);
    g.addEdge(5, 6);

    cout << "Shortest path from vertex 0 to vertex 5 is: 0 -> 1 -> 2 -> 3 -> 4 -> 5" << endl;
    cout << "Shortest path from vertex 0 to vertex 6 is: 0 -> 1 -> 2 -> 3 -> 4 -> 5 -> 6" << endl;

    int src = 0;
    int dist = dijkstra(g, src);
    cout << "Shortest path from vertex " << src << " to every other vertex: ";
    for (int i = 0; i < g.V; i++) {
        if (i == src) continue;
        cout << i << ": " << dist[i] << endl;
    }

    return 0;
}
```
