```go
package main

import (
	"fmt"
)

type Queue struct {
	front, rear *Stack
}

type Stack struct {
	items []interface{}
}

func (s *Stack) Push(item interface{}) {
	s.items = append(s.items, item)
}

func (s *Stack) Pop() (interface{}, bool) {
	if len(s.items) == 0 {
		return nil, false
	}
	item := s.items[len(s.items)-1]
	s.items = s.items[:len(s.items)-1]
	return item, true
}

func NewQueue() *Queue {
	return &Queue{
		front: &Stack{},
		rear:  &Stack{},
	}
}

func (q *Queue) Enqueue(item interface{}) {
	q.front.Push(item)
	if q.rear.Len() == 0 {
		q.rear.Push(item)
	}
}

func (q *Queue) Dequeue() (interface{}, bool) {
	if q.front.Len() == 0 {
		return q.rear.Pop()
	}
	item := q.front.Pop()
	q.front.Push(item)
	return item, true
}

func main() {
	q := NewQueue()
	q.Enqueue(1)
	q.Enqueue(2)
	q.Enqueue(3)
	fmt.Println("Front:", q.front.Items())
	fmt.Println("Rear:", q.rear.Items())
	fmt.Println("Dequeue:", q.Dequeue())
	fmt.Println("Front:", q.front.Items())
	fmt.Println("Rear:", q.rear.Items())
	fmt.Println("Dequeue:", q.Dequeue())
}
```
