        for (int i = 0; i <= m; i++) {
            for (int j = 0; j <= n; j++) {
                if (dp[i][j] > dp[i][j - 1] + dp[i - 1][j]) {
                    lcs = dp[i][j];
                }
            }
        }
        return lcs;
    }
